import React from "react";

const MovieTabs = props => {
	const{sort_by, updateSortBy}=props;
	handleClick=value=>{
		return(
		updateSortBy(value))
	}
  return (
    <ul className="tabs nav nav-pills">
      <li className="nav-item">
        <div className={`nav-link ${sort_by==="popularity.desc" ? "active" : ""}`} onClick={
     handleClick("popularity.desc")}>
          Popularity desc
        </div>
      </li>
      <li className="nav-item">
        <div className={`nav-link ${sort_by==="revenue.desc" ? "active" : ""}`} onClick={()=>
     updateSortBy("revenue.desc")}>Revenue desc</div>
      </li>
      <li className="nav-item">
        <div className={`nav-link ${sort_by==="vote_avetage.desc" ? "active" : ""}`} onClick={()=>
     updateSortBy("vote_avetage.desc")}>Vote avetage desc</div>
      </li>
    </ul>
  );
};

export default MovieTabs;
