import React from "react";


const Selectyear=props=>{
  const {sort_year}=props;

  const select_year=add_year=>{
    return()=>{
    sort_year(add_year);}
  }


    return(
<select size="3" multiple name="hero[]">
    <option disabled>Выберите год</option>
    <option onClick={select_year(2020)}>2020</option>
    <option onClick={select_year(2018)}>2018</option>
    <option onClick={select_year(2017)}>2017</option>
   </select>
    )

}




export default Selectyear;
