import React from "react";

const MovieItem = props=>{
	const{movie,removeMovie,addmovieToWillWatch}=props;
	 return (<div className="card">
	    <img
     className="card-img-top"
     src={`https://image.tmdb.org/t/p/w500${movie.backdrop_path ||
       movie.poster_path}`} 
     alt=" " />
        <div className="card-body">
     <h6 className="card-title">{props.movie.title}</h6>
     <div className="d-flex justify-content-between align-items-center">
       <p className="mb-0">Rating: {movie.vote_average}</p>
       <button type="button" className="btn btn-secondary" onClick={props.addmovieToWillWatch.bind(null,props.movie)}>
         Will Watch
       </button>
     </div>
		 <button onClick={props.removeMovie.bind(props, props.movie)}>Delete item</button>
</div>
</div>
		 )
}
	 
	 
	export default MovieItem;
